    updating=true;
    cset=set_number;

    char dummy[2048];
    plotarr p;
    get_graph_plotarr(graph_number, set_number, &p);

    redisplayContents();//to switch the decimal separator

    tabLi->cmbSet->update_entries(graph_number,true);

