void dialogScrollArea::keyPressEvent(QKeyEvent * k)
{
    switch (k->key())
    {
    case Qt::Key_Up:
    case Qt::Key_Down:
//qDebug() << "Parent KeyPressed=" << k->key();
        k->ignore();
        break;
    default:
//qDebug() << "Scroll Area KeyPressed=" << k->key();
        k->accept();
        QScrollArea::keyPressEvent(k);
        break;
    }
}

