    case 1:                     /* all axes, current graph */
        axis_start = 0;
        axis_stop  = MAXAXES - 1;
        graph_start = cg;
        graph_stop  = cg;
        break;
    case 2:                     /* current axis, all graphs */
        axis_start = curaxis;
        axis_stop  = curaxis;
        graph_start = 0;
        graph_stop  = number_of_graphs() - 1;
        break;
    case 3:                     /* all axes, all graphs */
        axis_start = 0;
        axis_stop  = MAXAXES - 1;
        graph_start = 0;
        graph_stop  = number_of_graphs() - 1;
        break;
    case 4:                     /* x+y axes, current graph */
        axis_start = 0;
        axis_stop  = 1;
        graph_start = cg;
        graph_stop  = cg;
        break;
    case 5:                     /* x+y axes, all graphs */
        axis_start = 0;
        axis_stop  = 1;
        graph_start = 0;
        graph_stop  = number_of_graphs() - 1;
        break;
    case 6:                     /* alt x+y axes, current graph */
        axis_start = 2;
        axis_stop  = 3;
        graph_start = cg;
        graph_stop  = cg;
        break;
    case 7:                     /* alt x+y axes, all graphs */
        axis_start = 2;
        axis_stop  = 3;
        graph_start = 0;
        graph_stop  = number_of_graphs() - 1;
